//create a class component called as TaskList
//in state maintain 2 tasks information 
//display the tasks in a table format
//export the component
import React from 'react'
class TaskList extends ReactComponent
{
    constructor()
    {
        super()
        this.state={
            tasks:[
                {id:1,name:'rashi',satus:false},
                {id:2,name:'riya',status:true}

            ]
        }
    }
    render()
    {
        return(
            <div>
                <table>
                    <thead>
                        <tr>
                            <th>ID </th>
                            <th>Name</th>
                            <th>Status</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.tasks.map(ele=>
                                {
                                    return(
                                        <tr>
                                        <td>{ele.id}</td>
                                        <td>{ele.name}</td>
                                        <td>{ele.status?'completed':'on-going'}</td>
                                        </tr>
                                    )
                                }
                                )
                            }
                    </tbody>
                </table>
            </div>
        )
    }
}
export default TaskList
